sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	// Initialise the PDF
	var v = new sn_pdfgeneratorutils.PDFGenerationAPI;

	// Get the variables out of the form
	var u_datarequest1       = current.u_data_request1;
	var u_datarequest1_hours = current.u_data_request1_hours;
	var u_datarequest2       = current.u_data_request2;
	var u_datarequest2_hours = current.u_data_request2_hours;


	//Get the HTML template referenced in the SOW table
	var html = current.template.template;

	// Lookup the rates for the requested skills
	var gr = new GlideRecord('x_58872_needit_ratecard');
	gr.addQuery('skillset', u_datarequest1);
	gr.query();
	if (gr.getRowCount() == 1)
		{
			gr.next();
		var skillset          = gr.skillset;
		var dailyRate         = gr.dailyrate;
		var discountDailyRate = gr.discountdailyrate;

		// Update the HTML to add in a new row
		var newRow = buildFeesRow(skillset, dailyRate, discountDailyRate, u_datarequest1_hours) + '<tr><td>newRow1</td></tr>';
		var updatedHTML = html.replace('<td>newRow</td>', newRow);
		var cost = parseFloat(dailyRate) * parseInt(u_datarequest1_hours);
		}

	gr = new GlideRecord('x_58872_needit_ratecard');
	gr.addQuery('skillset', u_datarequest2);
	gr.query();
	if (gr.getRowCount() == 1)
		{
			gr.next();
			skillset          = gr.skillset;
			dailyRate         = gr.dailyrate;
			discountDailyRate = gr.discountdailyrate;

		// Update the HTML to add in a new row
		newRow = buildFeesRow(skillset, dailyRate, discountDailyRate, u_datarequest2_hours);
		updatedHTML = updatedHTML.replace('<td>newRow1</td>', newRow);
		cost = cost + parseFloat(dailyRate) * parseInt(u_datarequest2_hours);
		}	

	//Create a version of the SOW cost in the right format
	//var cost = parseFloat(current.cost);
	//var sowCost = cost.toFixed(2);
	var sowCost = floatToCurrency(cost);
	current.cost = cost;


	//Update the HTML to replace the cost with the calculated cost
	//updatedHTML = html.replace('__totalCost__', current.cost);
	updatedHTML = updatedHTML.replace('$__totalCost__', sowCost);

	// Update the HTML to add in a new row
	//var newRow = buildFeesRow('New Skill', '1480', '1100', '5');
	//updatedHTML = updatedHTML.replace('<td>newRow</td>', newRow);

	//Set the table name where we're going to attach the PDF
	var tableName = 'x_58872_needit_my_custom_table';

	//Set the record to which the PDF is to be attached
	var targetTableSysid = current.getValue('sys_id');

	//Set the filename for the PDF
	var fileName = current.u_description_2;

	//Create the PDF
	var result = v.convertToPDF(updatedHTML, tableName, targetTableSysid, fileName);

    //Store the result of the call back in the table
	//current.pdfgenerationresult = u_datarequest2;
	current.pdfgenerationresult = JSON.stringify(result);
	//current.pdfgenerationresult = JSON.stringify(newRow);
	//current.pdfgenerationresult = updatedHTML;

	//Store the total number of days requested
	//current.total_hours = current.u_datarequest1_hours + current.u_datarequest2_hours;
	current.total_hours = current.u_data_request1_hours + current.u_data_request2_hours;
	//current.total_hours = u_datarequest1_hours + u_datarequest2_hours;

	current.update;



})(current, previous);
